abstract class PluginDmCatalogue extends BaseDmCatalogue
{

  public function hasTranslationFor($source)
  {
    return dmDb::query('DmTransUnit')
    ->where('cat_id = ?', $this->get('id'))
    ->andWhere('source = ?', $source)
    ->exists();
  }
}